    const slogan={
        display:'flex',
        flex:'row',
        alignItems:'center',
        justifyContent : 'space-between'
    }
    const slogan_img={
        width:'500px',
        height : '400px'
    }
    const slogan_h1 ={
        fontSize:'50px',
        fontWeight:'bolder'
    }
    const slogan_p ={
        fontWeight:'bold',
    }
    const trendingAuction ={
        display:'flex',
        flex:'column',

    }

    return (
      <div style={slogan} className="sloganPart">
          <div style={trendingAuction} className="trendingAuction">
          <div className="slogan_text">
          <h1 style={slogan_h1}>Paint Us with Your NFTs</h1>
          <p style={slogan_p}>Pi-market is an NFT market where you can mint and freely<br/>
          purchase NFT works related to the school you belong to.<br/>
          Sell and purchase any works of art, including photos, pictures,<br/>
          videos, music, etc. from your beloved school.</p>
          
          </div>
          <h3>Trending Auction</h3>
          </div>
          <img style={slogan_img} src={wave_img}/>
      </div>
    );
  }
  
  export default sloganPart;
